import React, {useState, useEffect, useCallback, useRef} from 'react'
import axios from 'axios'
import PAGECONST from './utils/Constants';
import Card from '../../components/molecules/layout/Card'

function MainContainer({}) { 
    const [paging, setPaging] = useState({})
    const [isLoading, setIsLoading] = useState(false)
    const [list, setList] = useState([])
    const [payload, setPayload] = useState({
        params: {
            page: 1,
            pageCount: PAGECONST.count,
            totalCount: 0
        }
    })
    const rootRef = useRef();
    const targetRef = useRef();

    const getData = () => {
      let key = '80CFeBE4MD6JmhEfClBx7zqo1eGvwTl5EZgKyMQc'
      axios.get("http://52.78.173.151/boards", {
        "x-api-key": key,
        })
        .then( response => {
         if(response){
           const { result, data } = response.data
           if(result.code === 1){
                setIsLoading(true)
                console.log('response ?',response)
                console.log('response.data !!',response.data)
                const paging = response.data.paging || paging;
                const list = response.data.data || list;
                setList(list)
                setPaging(paging)
                setIsLoading(false)
           }
         }
        })
        .catch(err => alert("글을 가져오는데 실패 했습니다."))
    }


    const handlerNaxtBtn = () =>{
      const { page } = payload.params;
      setPayload({

        ...payload,
        params: {
          page: page + 1,
        }
      })
      console.log('payload',payload)


      // for (let i = 1; i <= Math.ceil(paging.totalCount / paging.count); i++){
      //   list.push(i+1)
      // }
      // const target = list
    }

    // const ListCallbackL = (list, observer) => {
    //   list.forEach(item => {


    //   });
    // }
    // const view = new IntersectionObserver(ListCallbackL,{
    //   root: rootRef,
    //   rootMargin: '0px',
    //   threshold: 0.5
    // })

    // view.observe(targetRef);



    useEffect(() => {
        getData();
    },[payload])
    useEffect(() =>{
      console.log('list # ',list)
      console.log('paging # ',paging)
    },[list, paging])

    return (
        <div ref={rootRef}>
          {list.map((el, i) => {

          return (<div>{el.ID}</div>);
            // <Card ref={i == list.length - 1 && !payload.params.totalCount ? targetRef : null}/>
            })
          }
          <button type="button" onClick={handlerNaxtBtn}>PUSH!</button>
          {isLoading && <p>Loading...</p>}
        </div>
    );
  }

export default MainContainer;
