        axios.get("http://52.78.173.151/boards", {
          "x-api-key": key,
          }, body)
          .then( res => {
             if(body.loadMore) setListInfo([...listInfo, ...res.data.listInfo]);
             else setListInfo(res.data.listInfo)
               })
          .catch(err => alert("글을 가져오는데 실패 했습니다."))
    }

    function handleLoadMore() {
      setLoading(true);
      // Some API call to fetch the next page
      loadNextPage(endCursor, pageSize).then((newPage) => {
        setLoading(false);
        setHasNextPage(newPage.hasNextPage);
        setItems([...items, newPage.items]);
      });
    }

    const infiniteRef = useInfiniteScroll({
      loading,
      hasNextPage,
      onLoadMore: handleLoadMore,
      scrollContainer,
    });

    // ...

    return (
      <List ref={infiniteRef}>
        {items.map((item) => (
          <ListItem key={item.key}>{item.value}</ListItem>
        ))}
        {loading && <ListItem>Loading...</ListItem>}
      </List>
    );
  }



