import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './utils/notice.module.scss'
import Header from '../../components/molecules/layout/layoutHeader/Header'

const NoticeDescContainer = ({match}) => {
    console.log('location',match.params.id )
    const [data, setData] = useState([]);
    const [payload, setPayload] = useState({
        params: {
            id: match.params.id
        }
    })

    const getData = () => {
        let key = '80CFeBE4MD6JmhEfClBx7zqo1eGvwTl5EZgKyMQc '
        axios.get("http://ec2-3-35-207-154.ap-northeast-2.compute.amazonaws.com/notice", {
          "x-api-key": key,
          params:{
            notice_id: match.params.id
          }
          })
          .then( response => {
           if(response){
             const { data } = response.data
             if(response.status === 200){
                 console.log('TEST',data)
                 const resData = response.data.data
                 setData(resData)
             }
           }
          })
          .catch(err => alert("글을 가져오는데 실패 했습니다."))
      }
    useEffect(() => {
        getData();
    },[payload])

    return(
        <div id="cpWrap" className={`${styles.wrap_cp} ${styles.wrap_notice}`}>
            <Header/>
            <main id="cpContent" className={`${styles.section_notice}`}>
                <div className={`${styles.tit_unit}`}>
                    <span className={`${styles.theme_info}`}>
                        <span className={`${styles.txt_tit}`}>신 통합회원 전환 시행 안내 (유예기간) 통합회원 정책 업데이트와 더불어 기존 일반 회원을 신통합회원 업데이트와 더불어 기존 일반 회원을 신통합회원 업데이트와 더불어 기존 일반 회원을 신통합회원</span>
                        <span className={`${styles.txt_date}`}>2020.12.31</span>
                    </span>
                </div>
                <div className={`${styles.cont_unit}`}>
                    <span className={`${styles.theme_info}`}>
                        <span className={`${styles.view_cont}`}></span>
                    </span>
                </div>
            </main>
        </div>
    );

}

export default NoticeDescContainer;
