import React, { useState } from 'react';
import styles from './card.module.scss'






const Card = ({ ListData, item }) => {
    const [list, setList] = useState(item);
    // const styles = {
    //     float: 'left',
    //     width: 'calc(50% - 2px)',
    //     height: '150px',
    //     // margin: '5px',

    // }
    const imageArea = {

        background: `url(${list.imageUrl})`,
        backgroundSize: '100px',
        backgroundPosition: 'center',
    }
    const fontStyle = {
        display: 'block',
        fontSize: 12,
    }


    return(
        <div className={`${styles.unit_card}`}>
            <div className={`${styles.area_img}`}>
                <span className={`${styles.img_desc}`} style={imageArea}></span>
            </div>
            <div className={`${styles.info_desc}`}>
                <span style={fontStyle}>{list.brandName}</span>
                <span style={fontStyle}>{list.content}</span>
                <span style={fontStyle}>{list.majorCategoryName}</span>
            </div>
        </div>  
    );
}

export default Card;
