import React, {useState, useEffect, useCallback, useRef} from 'react'
import useInfiniteScroll from 'react-infinite-scroll-hook';
import axios from 'axios'
import Card from './Card'
// import Infinite from './Infinite'

function ListView({}) {
    // instance variable
    const currentPage = useRef(1);
    const totalPage = useRef(0);

    // request state
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // contents list
    const [images, setImages] = useState([]);

    // ref
    const rootRef = useRef(null);
    const targetRef = useRef(null);



    const UnsplashAPI = (body) => {
        let key = '80CFeBE4MD6JmhEfClBx7zqo1eGvwTl5EZgKyMQc'
        axios.get("http://52.78.173.151/boards", {
          "x-api-key": key,
          }, body)
          .then( res => {
             if(body.loadMore) setListInfo([...listInfo, ...res.data.listInfo]);
             else setListInfo(res.data.listInfo)
               })
          .catch(err => alert("글을 가져오는데 실패 했습니다."))
    }

    const loadImage = useCallback(async ({ query, page }) => {
        try {
          setLoading(true);
          const data = await UnsplashAPI.searchPhotos({ query, page, per_page: PER_PAGE});
          totalPage.current = data.total_pages;
          return data;
        } catch(e) {
          setError(e);
        } finally {
          setLoading(false);
        }
    }, []);

    const searchImage = useCallback(async (query) => {
      if(!query) {
        await loadRandomImage();
        return;
      }
      currentQuery.current = query;
      currentPage.current = 1;
      const data = await loadImage({ query, page: 1, per_page: PER_PAGE });
      setImages(data.results);
    }, [loadImage, loadRandomImage]);

    const loadMoreImage = useCallback(async () => {
      if(images.length > 0) {
        currentPage.current++;
        const data = await loadImage({
          query: currentQuery.current,
          page: currentPage.current
        });
        setImages([...images, ...data.results])
      }
    },[images, loadImage]);


    return (
        <div ref={rootRef}>
            <div ref={targetRef} />
        </div>
